              public function GetInitialStateAttributeFlags($sAttCode, &$aReasons = array())
              {
                  if (in_array($sAttCode, ['webhook', 'webhook_status', 'event_count', 'last_event_date', 'github_webhook_configuration'])) {
                      return OPT_ATT_HIDDEN;
                  }
                  return parent::GetInitialStateAttributeFlags($sAttCode, $aReasons);
              }





          false
          public
          Overload-DBObject

              public function GetAttributeFlags($sAttCode, &$aReasons = array(), $sTargetState = '')
              {
                  if($sAttCode === 'last_event_date' && $this->Get('event_count') == 0){
                      return OPT_ATT_HIDDEN;
                  }

                  if (in_array($sAttCode, ['name', 'webhook', 'webhook_status', 'event_count', 'last_event_date'])) {
                      return OPT_ATT_READONLY;
                  }

                  if($sAttCode === 'github_webhook_configuration' && utils::IsNullOrEmptyString($this->Get('github_webhook_configuration'))){
                      return OPT_ATT_HIDDEN;
                  }

                  return parent::GetAttributeFlags($sAttCode, $aReasons, $sTargetState);
              }





