    /* VCS Log Attribute Automation *///////////////////////////////////////////////////////////////////////////////////////////////


      AbstractVCSAutomation

        bizmodel,searchable
        false
        autoincrement
        vcs_log_attribute_automation
        id




          %1$s













          object_class
